import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import FriendsBar from '../components/common/FriendsBar';

const ProfilePage = () => {
    const navigate = useNavigate();
    const [username, setUsername] = useState('Guest');
    const [userId, setUserId] = useState(null);
    const [friendRequests, setFriendRequests] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchUserData = async () => {
            const { data: { user }, error } = await supabase.auth.getUser();

            if (error) {
                console.error('Error fetching user data:', error.message);
                navigate('/');
                return;
            }

            if (!user) {
                navigate('/');
                return;
            }

            setUserId(user.id);
            fetchUserProfile(user.id);
            fetchFriendRequests(user.id);
        };

        fetchUserData();
    }, [navigate]);

    const fetchUserProfile = async (userId) => {
        const { data: profile, error } = await supabase
            .from('profiles')
            .select('username')
            .eq('user_id', userId)
            .single();

        if (error) {
            console.error('Error fetching profile data:', error.message);
            return;
        }

        if (profile) {
            setUsername(profile.username);
        }
    };

    const fetchFriendRequests = async (userId) => {
        const { data, error } = await supabase
            .from('friend_requests')
            .select(`
                id,
                status,
                sender:sender_id (username)
            `)
            .eq('receiver_id', userId)
            .eq('status', 'pending');

        if (error) {
            console.error('Error fetching friend requests:', error.message);
            return;
        }

        setFriendRequests(data);
    };


    const acceptFriendRequest = async (requestId) => {
        const request = friendRequests.find(request => request.id === requestId);
        if (!request) return;

        try {
            const { error } = await supabase
                .from('friends')
                .insert([
                    { user_id: userId, friend_id: request.sender_id }
                ]);

            if (error) throw error;

            // Update friend request to accepted
            await supabase
                .from('friend_requests')
                .update({ status: 'accepted' })
                .eq('id', requestId);

            alert('Friend request accepted!');
        } catch (error) {
            console.error('Error accepting friend request:', error.message);
        }
    };

    const rejectFriendRequest = async (requestId) => {
        try {
            const { error } = await supabase
                .from('friend_requests')
                .update({ status: 'rejected' })
                .eq('id', requestId);

            if (error) throw error;

            alert('Friend request rejected!');
        } catch (error) {
            console.error('Error rejecting friend request:', error.message);
        }
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <div className="w-full h-screen flex flex-col items-center justify-start pt-20">
            <h1 className="text-2xl text-center">
                Welcome back, <span className="underline">{username}</span>
            </h1>
            <FriendsBar userId={userId} />
            <div>
                <h2>Pending Friend Requests:</h2>
                {friendRequests.map(request => (
                    <div key={request.id}>
                        <p>{request.sender.username}</p>
                        <button onClick={() => acceptFriendRequest(request.id)}>Accept</button>
                        <button onClick={() => rejectFriendRequest(request.id)}>Reject</button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ProfilePage;
