import React, { useState, useEffect } from 'react';
import { supabase } from '../../supabaseClient';

const ShareWatchlist = ({ onShare }) => {
    const [friends, setFriends] = useState([]);
    const [selectedFriend, setSelectedFriend] = useState('');

    useEffect(() => {
        fetchFriends();
    }, []);

    const fetchFriends = async () => {
        const { data: { user } } = await supabase.auth.getUser();
        if (!user) return;

        const { data: friendsData, error } = await supabase
            .from('friends')
            .select('friend_id(username)')
            .eq('user_id', user.id)
            .eq('status', 'active');

        if (error) {
            console.error('Error fetching friends:', error.message);
        } else {
            setFriends(friendsData.map(friend => ({
                id: friend.friend_id,
                username: friend.friend_id.username // Assuming the username is directly accessible
            })));
        }
    };

    const handleShare = () => {
        if (selectedFriend) {
            onShare(selectedFriend);
            setSelectedFriend('');  // Reset selection
        } else {
            alert('Please select a friend to share the watchlist with.');
        }
    };

    return (
        <div>
            <select value={selectedFriend} onChange={(e) => setSelectedFriend(e.target.value)} className="border p-2 rounded">
                <option value="">Select a friend</option>
                {friends.map(friend => (
                    <option key={friend.id} value={friend.id}>
                        {friend.username}
                    </option>
                ))}
            </select>
            <button onClick={handleShare} className="ml-2 btn bg-blue-500 hover:bg-blue-700 text-white p-2 rounded">
                Share Watchlist
            </button>
        </div>
    );
};

export default ShareWatchlist;
