import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../components/common/header'; // Reusing the same header for consistency
import { createClient } from '@supabase/supabase-js';

// Initialize Supabase client
const supabaseUrl = 'https://klligruouvwvqrwrxqcj.supabase.co';
const supabaseAnonKey = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6ImtsbGlncnVvdXZ3dnFyd3J4cWNqIiwicm9sZSI6ImFub24iLCJpYXQiOjE3MDc1MDg5NTQsImV4cCI6MjAyMzA4NDk1NH0.9DI6I1eD9mr6wzlvePSMTdr6DQctU4Mh9vGv8XD8WJ4'; // Replace with your Supabase anon key
const supabase = createClient(supabaseUrl, supabaseAnonKey);

async function getUserInfo() {
    let { data: profiles, error } = await supabase
        .from('profiles')
        .select('first_name, last_name, location, employee');

    if (error) throw error;
    if (profiles.length === 0) throw new Error("No profile found");

    return profiles[0]; // Assuming you want the first profile's information
}

const ProfilePage = () => {
    const [profile, setProfile] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        getUserInfo()
            .then(profile => {
                setProfile(profile);
                setLoading(false);
            })
            .catch(error => {
                setError(error.message);
                setLoading(false);
            });
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>; // Handling error state
    }

    return (
        <div className="min-h-screen flex flex-col bg-black">
            <Header />
            <div className="flex-grow container mx-auto p-4 text-center">
                <h1 className="text-white font-extrabold text-4xl md:text-6xl lg:text-7xl tracking-tight mt-3">Profile</h1>
                <div className="mt-10">
                    <p className="text-white font-bold text-xl md:text-3xl">Welcome, {profile.first_name}</p>
                    <p className="text-white text-lg md:text-xl">Location: {profile.location}</p>
                    <p className="text-white text-lg md:text-xl">Employee: {profile.employee ? 'Yes' : 'No'}</p>
                </div>
                {/* Create a form to update the profile */}
                {/* First, text inputs */}f
                <div className="mt-10">
                    <form className="max-w-md mx-auto">
                        <div className="mb-4">
                            <input type="text" value={profile.first_name} placeholder="First Name" className="w-full p-2 border border-gray-300 rounded" required />
                        </div>
                        <div className="mb-4">
                            <input type="text" value={profile.last_name} placeholder="Last Name" className="w-full p-2 border border-gray-300 rounded" required />
                        </div>
                        <div className="mb-4">
                            <input type="text" value={profile.location} placeholder="Location" className="w-full p-2 border border-gray-300 rounded" required />
                        </div>
                        <div className="mb-6">
                            <input type="text" value={profile.employee} placeholder="Employee" className="w-full p-2 border border-gray-300 rounded" required />
                        </div>
                        <button type="submit" className="w-full bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">Update Profile</button>
                    </form>
                </div>
            </div>
            <footer className="bg-gray-200 text-center p-4">
                Â© 2024 Bruh (bRUH), Inc.
            </footer>
        </div>
    );
};

export default ProfilePage;
