import React, { useState, useEffect } from 'react';
import { supabase } from '../../supabaseClient';

const ShareWatchlist = ({ onShare, userId }) => {
    const [friends, setFriends] = useState([]);
    const [selectedFriend, setSelectedFriend] = useState('');

    useEffect(() => {
        if (!userId) return;  // Only fetch if userId is available
        fetchFriends();
    }, [userId]);  // Add userId as a dependency

    const fetchFriends = async () => {
        const { data: friendsData, error } = await supabase
            .from('friends')
            .select(`
                friend_id,
                profile:friend_id (username)  // Fetching username from the related profiles table
            `)
            .eq('user_id', userId)
            .eq('status', 'active');

        console.log('Fetched friends:', friendsData);  // Debugging (optional
        if (error) {
            console.error('Error fetching friends:', error.message);
        } else {
            setFriends(friendsData.map(friend => ({
                id: friend.friend_id,
                username: friend.profile.username  // Ensure 'profile' is the correct relationship alias
            })));
        }
    };

    const handleShare = () => {
        if (selectedFriend) {
            onShare(selectedFriend);
            setSelectedFriend('');  // Reset selection after sharing
        } else {
            alert('Please select a friend to share the watchlist with.');
        }
    };

    console.log("FRIENDS", friends)
    return (
        <div>
            <select value={selectedFriend} onChange={(e) => setSelectedFriend(e.target.value)} className="border p-2 rounded">
                <option value="">Select a friend</option>
                {friends.map(friend => (
                    <option key={friend.id} value={friend.id}>
                        {friend.username}
                    </option>
                ))}
            </select>
            <button onClick={handleShare} className="ml-2 btn bg-blue-500 hover:bg-blue-700 text-white p-2 rounded">
                Share Watchlist
            </button>
        </div>
    );
};

export default ShareWatchlist;
