import React, { useState, useEffect } from 'react';
import { supabase } from '../../supabaseClient';

const FriendRequestsDropdown = ({ userId }) => {
    const [requests, setRequests] = useState([]);
    const [showDropdown, setShowDropdown] = useState(false);

    useEffect(() => {
        if (!userId) return;

        const fetchRequests = async () => {
            const { data, error } = await supabase
                .from('friend_requests')
                .select(`
                    id,
                    status,
                    sender:sender_id (username)  // Assuming correct foreign key setup
                `)
                .eq('receiver_id', userId)
                .eq('status', 'pending');

            if (error) {
                console.error('Error fetching friend requests:', error.message);
            } else {
                setRequests(data || []);
            }
        };

        fetchRequests();
    }, [userId]);

    const handleResponse = async (requestId, status) => {
        const { error: updateError, data: updateData } = await supabase
            .from('friend_requests')
            .update({ status })
            .match({ id: requestId });

        if (updateError) {
            console.error('Error updating friend request:', updateError.message);
            return;  // Stop further execution in case of error
        }

        if (status === 'accepted') {
            // Find the sender_id from the requests to add to the friends table
            const request = requests.find(req => req.id === requestId);
            if (!request) {
                console.error('Request not found');
                return;
            }

            if (!request.sender_id) {
                console.error('Sender ID is undefined');
                return;
            }

            console.log('Handling friend acceptance for:', request.sender_id, ' and ', userId);

            const { error: insertError } = await supabase
                .from('friends')
                .insert([
                    { user_id: userId, friend_id: request.sender_id, status: 'active' },
                    { user_id: request.sender_id, friend_id: userId, status: 'active' }  // This ensures that the friendship is bidirectional
                ]);

            if (insertError) {
                console.error('Error adding friend:', insertError.message);
            } else {
                // Update requests state to remove the handled request
                setRequests(prev => prev.filter(req => req.id !== requestId));
            }
        } else {
            // Just remove the request for 'rejected'
            setRequests(prev => prev.filter(req => req.id !== requestId));
        }
    };



    return (
        <div>
            <button onClick={() => setShowDropdown(!showDropdown)} className="btn">
                Friend Requests ({requests.length})
            </button>
            {showDropdown && requests.length > 0 && (
                <div className="absolute bg-white shadow-md mt-2 rounded">
                    <ul>
                        {requests.map(request => (
                            <li key={request.id} className="p-2 border-b border-gray-200">
                                Request from {request.sender ? request.sender.username : "Unknown"}
                                <button onClick={() => handleResponse(request.id, 'accepted')} className="ml-2 btn bg-green-500 hover:bg-green-600">
                                    Accept
                                </button>
                                <button onClick={() => handleResponse(request.id, 'rejected')} className="ml-2 btn bg-red-500 hover:bg-red-600">
                                    Reject
                                </button>
                            </li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default FriendRequestsDropdown;
