import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { sortableContainer, sortableElement } from 'react-sortable-hoc';
import arrayMove from 'array-move';
import { supabase } from '../supabaseClient';
import MovieCard from '../components/common/MovieCard';
import Search from '../components/common/MovieSearch';  // Ensure path is correct

const SortableItem = sortableElement(({ item }) => (
  <MovieCard
    key={item.id}
    id={item.id}
    title={item.title}
    medium={item.medium}
    length={item.length}
    date={item.release_date.substring(0, 4)}
    synopsis={item.synopsis}
    image={item.image}
    onDelete={() => handleDeleteMediaItem(item.id, item.medium)}
  />
));

const SortableList = sortableContainer(({ items }) => {
  return (
    <div>
      {items.map((item, index) => (
        <SortableItem key={`item-${index}`} index={index} item={item} />
      ))}
    </div>
  );
});

const MediaPage = () => {
    const [mediaItems, setMediaItems] = useState([]);
    const { username, watchlistName } = useParams();

    useEffect(() => {
        fetchMediaItems();
    }, [watchlistName]);

    const fetchMediaItems = async () => {
        const { data: watchlist } = await supabase
            .from('watchlists')
            .select('id')
            .eq('name', watchlistName)
            .single();

        if (watchlist) {
            const { data: media } = await supabase
                .from('media_items')
                .select('*')
                .eq('watchlist_id', watchlist.id)
                .order('order', { ascending: true });

            setMediaItems(media || []);
        }
    };

    const onSortEnd = async ({ oldIndex, newIndex }) => {
        const newItems = arrayMove(mediaItems, oldIndex, newIndex);
        setMediaItems(newItems);

        // Update the order in the database
        for (let i = 0; i < newItems.length; i++) {
            await supabase
                .from('media_items')
                .update({ order: i })
                .match({ id: newItems[i].id });
        }
    };

    return (
        <div className="container mx-auto p-4">
            <h1 className="text-xl font-bold">{`Media in "${watchlistName}"`}</h1>
            <Search onSelect={handleSelectItem} />
            <SortableList items={mediaItems} onSortEnd={onSortEnd} useDragHandle />
        </div>
    );
};

export default MediaPage;
