import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../../supabaseClient';

const ProfileHeader = ({ username }) => {
    const navigate = useNavigate();
    const [friendName, setFriendName] = useState('');
    const [showDropdown, setShowDropdown] = useState(false);

    const handleLogout = async () => {
        await supabase.auth.signOut();
        navigate('/');
    };

    const searchFriends = async (event) => {
        setFriendName(event.target.value);
        // Add logic here to search user database and update a state with results
    };

    return (
        <header className="bg-zinc-700 text-white p-4 shadow-md">
            <div className="container mx-auto flex justify-between items-center">
                <h1 className="font-bold text-2xl">Profile: {username}</h1>
                <div>
                    <input
                        type="text"
                        placeholder="Enter friend's name"
                        value={friendName}
                        onChange={searchFriends}
                        className="border p-2"
                    />
                    <button onClick={() => setShowDropdown(!showDropdown)} className="ml-2 btn">Search</button>
                    {showDropdown && (
                        <div className="absolute mt-1 w-52 bg-white shadow-lg">
                            <ul>
                                {/* Map search results here */}
                                <li className="p-2 hover:bg-gray-200 cursor-pointer">Friend Name</li>
                            </ul>
                        </div>
                    )}
                </div>
                <button onClick={handleLogout} className="ml-4 btn bg-red-500 hover:bg-red-600">Logout</button>
            </div>
        </header>
    );
};

export default ProfileHeader;
