import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import MovieCard from '../components/common/MovieCard';
import Search from '../components/common/MovieSearch';  // Ensure path is correct

const MediaPage = () => {
    const [mediaItems, setMediaItems] = useState([]);
    const { username, watchlistName } = useParams();

    useEffect(() => {
        fetchMediaItems();
    }, [watchlistName]);

    const fetchMediaItems = async () => {
        const { data: watchlist } = await supabase
            .from('watchlists')
            .select('id')
            .eq('name', watchlistName)
            .single();

        if (watchlist) {
            const { data: media } = await supabase
                .from('media_items')
                .select('*')
                .eq('watchlist_id', watchlist.id);

            setMediaItems(media || []);
        }
    };

    const handleSelectItem = async (item) => {
        // Fetch additional details including the image URL
        const detailsUrl = `https://api.themoviedb.org/3/${item.media_type}/${item.id}?api_key=89d44f8db4046fedba0c0d1a0ab8fc74&append_to_response=credits`;
        const response = await fetch(detailsUrl);
        const details = await response.json();

        const imageUrl = details.poster_path ? `https://image.tmdb.org/t/p/w500${details.poster_path}` : '';

        const { data: newMedia, error } = await supabase
            .from('media_items')
            .insert([{
                title: item.title || item.name,
                medium: item.media_type === 'movie' ? 'Movie' : 'TV',
                watchlist_id: (await supabase.from('watchlists').select('id').eq('name', watchlistName).single()).data.id,
                image: imageUrl,  // Store the image URL
                release_date: item.release_date || ''
            }])
            .select();

        if (error) {
            console.error('Failed to add item:', error.message);
        } else {
            setMediaItems([...mediaItems, ...newMedia]);
        }
    };

    const handleDeleteMediaItem = async (deletedId, medium) => {
        if (window.confirm(`Are you sure you want to delete this ${medium}?`)) {
            const { error } = await supabase
                .from('media_items')
                .delete()
                .match({ id: deletedId });

            if (error) {
                console.error('Error deleting media item:', error.message);
            } else {
                setMediaItems(currentMediaItems => currentMediaItems.filter(item => item.id !== deletedId));
            }
        }
    };


    return (
        <div className="container mx-auto p-4">
            <h1 className="text-xl font-bold">{`Media in "${watchlistName}"`}</h1>
            <Search onSelect={handleSelectItem} />
            {mediaItems.map((item) => (
                <MovieCard 
                    key={item.id} 
                    id={item.id} 
                    title={item.title} 
                    medium={item.medium} 
                    length={item.length} 
                    date={item.release_date.substring(0, 4)} 
                    synopsis={item.synopsis}
                    image={item.image}  // Modified
                    onDelete={() => handleDeleteMediaItem(item.id, item.medium)}  // Modified
                />
            ))}
        </div>
    );
};

export default MediaPage;
