import React, { useState } from 'react';
import { Draggable } from 'react-beautiful-dnd';
import { supabase } from '../../supabaseClient';

const DragHandle = ({ id, index }) => (
  <Draggable draggableId={id.toString()} index={index}>
    {(provided) => (
      <div
        ref={provided.innerRef}
        {...provided.draggableProps}
        {...provided.dragHandleProps}>
        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" className="cursor-move w-6 h-6">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 8h16M4 16h16"/>
        </svg>
      </div>
    )}
  </Draggable>
);

const MovieCard = ({ id, title, medium, length, date, synopsis, image, url, onDelete, index }) => {
    const [open, setOpen] = useState(false);

    return (
        <div className="movie-card p-2 overflow-hidden flex flex-col">
            <div className="movie-card-header w-auto justify-between flex items-center">
                <DragHandle id={id} index={index} />
                <div className="item-title font-bold flex-1">{title} ({date})</div>
                <button onClick={() => setOpen(!open)}>{open ? 'Hide' : 'Show'} Details</button>
                <button onClick={() => onDelete(id)}>Delete</button>
            </div>
            {open && (
                <div>
                    <p>{synopsis}</p>
                    <p>{length} minutes</p>
                </div>
            )}
        </div>
    );
};

export default MovieCard;
