import React, { useState } from 'react';

const MovieCard = ({ id, title, medium, length, date, synopsis, director, year, onDelete }) => {
    const [open, setOpen] = useState(false);

    const handleDelete = async () => {
        if (window.confirm(`Are you sure you want to delete ${title}?`)) {
            onDelete(id);
        }
    };

    return (
        <div className="movie-card p-2 overflow-hidden flex flex-col border rounded">
            <div className="movie-card-header flex justify-between items-center p-2">
                <div className="item-title font-bold text-xl">{`${title} ${year ? `(${year})` : ''} - ${director}`}</div>
                <div>
                    <button onClick={handleDelete} className="text-red-500">Delete</button>
                    <button onClick={() => setOpen(!open)}>{open ? 'Hide' : 'Show'}</button>
                </div>
            </div>
            {open && (
                <div className="p-2">
                    <p>Medium: {medium}</p>
                    <p>Length: {length} minutes</p>
                    <p>Date Added: {date}</p>
                    <p>Synopsis: {synopsis}</p>
                </div>
            )}
        </div>
    );
};

export default MovieCard;
