import React, { useState } from 'react';
import { supabase } from '../../supabaseClient';  // Adjust path as needed

const FriendsBar = () => {
    const [friendName, setFriendName] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    const [showDropdown, setShowDropdown] = useState(false);

    const searchFriends = async (event) => {
        const name = event.target.value;
        setFriendName(name);
        if (name.length >= 3) { // Ensure at least 3 characters are typed before searching
            try {
                const { data, error } = await supabase
                    .from('profiles')
                    .select('username, user_id')
                    .ilike('username', `%${name}%`);

                if (error) {
                    throw error;
                }

                setSearchResults(data);
                setShowDropdown(true);
            } catch (error) {
                console.error('Error searching for friends:', error.message);
                setSearchResults([]);
            }
        } else {
            setSearchResults([]);
            setShowDropdown(false);
        }
    };

    return (
        <div className="relative">
            <input
                type="text"
                placeholder="Enter friend's name"
                value={friendName}
                onChange={searchFriends}
                className="border p-2"
            />
            {showDropdown && searchResults.length > 0 && (
                <div className="absolute mt-1 w-52 bg-white shadow-lg">
                    <ul>
                        {searchResults.map(friend => (
                            <li key={friend.user_id} className="p-2 hover:bg-gray-200 cursor-pointer">
                                {friend.username}
                            </li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default FriendsBar;
