import React, { useState } from 'react';

const MovieCard = ({ id, title, medium, length, date, synopsis }) => {
    const [open, setOpen] = useState(false);

    return (
        <div className="movie-card p-2 overflow-hidden flex flex-col">
            <div className="movie-card-header w-auto justify-between flex items-center">
                <div className="item-title font-bold px-3 py-2 text-lg">{`${title} (${date})`}</div>
                <button onClick={() => setOpen(!open)} className="py-1 px-2 text-sm">Details</button>
            </div>
            {open && (
                <div className="p-2">
                    <p>Length: {length}</p>
                    <p>Synopsis: {synopsis}</p>
                </div>
            )}
        </div>
    );
};

export default MovieCard;
