import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import ProfileHeader from '../components/common/ProfileHeader';
import FriendsBar from '../components/common/FriendsBar';

const ProfilePage = () => {
    const navigate = useNavigate();
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchUserData = async () => {
            const { data: { user }, error } = await supabase.auth.getUser();

            if (error) {
                console.error('Error fetching user data:', error.message);
                navigate('/');
                return;
            }

            if (!user) {
                navigate('/');
                return;
            }

            setUser(user);
            setLoading(false);
        };

        fetchUserData();
    }, [navigate]);

    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <div className="w-full h-screen flex flex-col items-center justify-start pt-20">
            <ProfileHeader username={user?.email} />
            <h1 className="text-2xl text-center">Welcome back, <span className="underline">{user?.email}</span></h1>
            <FriendsBar userId={user.id} />
        </div>
    );
};

export default ProfilePage;
