import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Header from '../components/common/header'; // Assuming the same header is used

const SignupPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        const { username, password } = credentials;

        // Using Supabase for authentication
        const { error } = await supabase.auth.signIn({
          email: username,
          password: password,
        });

        if (error) {
          setError(error.message);
        } else {
          // Login successful
          console.log('Login successful!');
          // Redirect or perform further actions
          setError(''); // Clear any errors
          // Reset form fields
          setCredentials({ username: '', password: '' });
        }
      };

    const handleGoogleSignIn = async () => {
        // Redirects the user to Google's sign-in page and then back to your app
        const { user, session, error } = await supabase.auth.signIn({
            provider: 'google',
        });

        if (error) {
            setError(error.message);
        } else {
            console.log('Google sign in successful:', user);
            // Redirect or perform further actions
            // For example, navigate to the homepage or dashboard
        }
    };

    return (
        <div className="min-h-screen flex flex-col bg-black">
            <Header />
            <div className="flex-grow container mx-auto p-4 text-center">
                <h1 className="text-white font-extrabold text-4xl md:text-6xl lg:text-7xl tracking-tight mt-3">Signup for an Account</h1>
                {error && <p className="text-red-500">{error}</p>}
                <div className="mt-10">
                    <form onSubmit={handleSubmit} className="max-w-md mx-auto">
                        <div className="mb-4">
                            <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" className="w-full p-2 border border-gray-300 rounded" required />
                        </div>
                        <div className="mb-6">
                            <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password" className="w-full p-2 border border-gray-300 rounded" required />
                        </div>
                        <button type="submit" className="w-full bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">Sign Up</button>
                    </form>
                    <p className="text-gray-300 mt-4">Or</p>
                    <button onClick={handleGoogleSignIn} className="w-full bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded mt-2">Sign Up with Google</button>
                    <p className="text-white mt-4">Already have an account? <Link to="/login" className="text-blue-500 hover:underline">Login</Link></p>
                </div>
            </div>
            <footer className="bg-gray-200 text-center p-4">
                Â© 2024 Bruh (bRUH), Inc.
            </footer>
        </div>
    );
};

export default SignupPage;
